import React, { useEffect, useState } from "react";
import { Select } from "antd";
import { Table, Tag, Space, Modal, Button, Tooltip } from "antd";
import Router from "next/router";
const { Option } = Select;
import { useRouter } from "next/router";

const MasterTables = () => {
  const [isModalVisibleStatus, setIsModalVisibleStatus] = useState(false);
  const [isModalVisible, setIsModalVisible] = useState(false);
  //   const [isModalVisible, setIsModalVisible] = useState(false);
  //   const [isModalVisible, setIsModalVisible] = useState(false);
  //   const [isModalVisible, setIsModalVisible] = useState(false);
  //   const [isModalVisible, setIsModalVisible] = useState(false);
  //   const [isModalVisible, setIsModalVisible] = useState(false);
  //   const [isModalVisible, setIsModalVisible] = useState(false);

  ///////////////////////////
  const [handleStatus, setHandleStatus] = useState("");
  const [commentStatus, setcommentStatus] = useState("");

  const [handle, setHandle] = useState("");
  const [comment, setcomment] = useState("");

  //   const [handle, setHandle] = useState("");
  //   const [comment, setcomment] = useState("");

  //   const [handle, setHandle] = useState("");
  //   const [comment, setcomment] = useState("");

  //   const [handle, setHandle] = useState("");
  //   const [comment, setcomment] = useState("");

  //   const [handle, setHandle] = useState("");
  //   const [comment, setcomment] = useState("");

  //   const [handle, setHandle] = useState("");
  //   const [comment, setcomment] = useState("");

  //   const [handle, setHandle] = useState("");
  //   const [comment, setcomment] = useState("");

  //////////////////////////
  const [Masstatus, setMasstatus] = useState([]);
  let arrstatus = Masstatus;
  const [Masquality, setMasquality] = useState([]);
  let arrquality = Masquality;
  const [type, settype] = useState([]);
  let arrtype = type;
  const [attribute, setattribute] = useState([]);
  let arrattribute = attribute;
  const [ind, setind] = useState([]);
  let arrind = ind;
  const [traffic, settraffic] = useState([]);
  let arrtraffic = traffic;
  const [Mascurrency, setMascurrency] = useState([]);
  let arrcurrency = Mascurrency;
  const [country, setcountry] = useState([]);
  let arrcountry = country;
  const [hasErrormas, setErrorsmas] = useState(false);
  const fetchDataMaster = () => {
    fetch("http://localhost:5000/api/v1/master/")
      .then(async (res) => {
        const data2 = await res.json();
        console.log(data2, "ddddddddddddddddd");
        setMasstatus(data2[0].Master_Table.split(","));
        setMasquality(data2[1].Master_Table.split(","));
        settype(data2[2].Master_Table.split(","));
        setattribute(data2[3].Master_Table.split(","));
        setind(data2[4].Master_Table.split(","));
        settraffic(data2[5].Master_Table.split(","));
        setMascurrency(data2[6].Master_Table.split(","));
        setcountry(data2[7].Master_Table.split(","));
      })

      .catch((err) => setErrorsmas(err));
  };
  useEffect(() => {
    console.log("componentDidMount");
    fetchDataMaster();
  }, [0]);


  const handleOk = () => {
      postData().then(()=>{})
    
  };

  const handleCancel = () => {
    setIsModalVisibleStatus(false);
  };

  const postData = () => {
    const statusRequest = {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        Master_Name: "STATUS",
        Master_Table: Masstatus,
        Master_Comments: commentStatus,
      }),
    };
    fetch("http://localhost:5000/api/v1/master/1", requestOptions)
      .then(async (response) => {
        const data = await response.json();
        if (!response.ok) {
          // get error message from body or default to response status
          const error = (data && data.message) || response.status;
          return Promise.reject(error);
        }
        console.log("data posted successfully");
      })
      .catch((error) => {
        this.setState({ errorMessage: error.toString() });
        console.error("There was an error!", error);
      });
    window.location.reload(false);
    // Router.push("MasterTables");
  };

  return (
    <div>
      <Modal
        title="Status"
        visible={isModalVisibleStatus}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        {arrstatus.map((el, key) => {
          return (
            <div style={{ display: "flex", marginBottom: "10px" }}>
              <div style={{ marginRight: "10px", marginTop: "5px" }}>{key}</div>
              <div style={{ border: "1px solid", padding: "5px" }}>{el}</div>
            </div>
          );
        })}
        <div style={{ display: "flex" }}>
          <div style={{ marginRight: "10px", marginTop: "5px" }}>
            {arrstatus.length}
          </div>
          <input
            style={{ border: "1px solid", padding: "5px" }}
            placeholder="Add a value here..."
            onChange={(e) => {
              setHandleStatus(e.target.value);
              console.log(typeof handleStatus);
            }}
          ></input>
          <Button
            style={{ marginLeft: "20px", marginTop: "10px" }}
            onClick={() => {
              arrstatus.push(handleStatus);
              setMasstatus(arrstatus);
              console.log(arrstatus);
              setIsModalVisibleStatus(false);
            }}
          >
            Add Value
          </Button>
        </div>

        <div style={{ marginTop: "20px", width: "100%" }}>
          <div>Status Comment</div>
          <input
            style={{ width: "100%" }}
            placeholder="Add your comment here..."
            onChange={(e) => {
              setcommentStatus(e.target.value);
            }}
          ></input>
        </div>
      </Modal>
      <h1>Masters Tables</h1>
      <div style={{ width: "60%", marginLeft: "auto", marginRight: "auto" }}>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            margin: "30px",
            padding: "10px",
            border: "2px solid ",
          }}
          onClick={() => {
            setIsModalVisibleStatus(true);
          }}
        >
          Status
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            margin: "30px",
            padding: "10px",
            border: "2px solid ",
          }}
        >
          Link Quality
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            margin: "30px",
            padding: "10px",
            border: "2px solid ",
          }}
        >
          Link Type
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            margin: "30px",
            padding: "10px",
            border: "2px solid ",
          }}
        >
          Link Attribute
        </div>

        <div
          style={{
            display: "flex",
            justifyContent: "center",
            margin: "30px",
            padding: "10px",
            border: "2px solid ",
          }}
        >
          Industry
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            margin: "30px",
            padding: "10px",
            border: "2px solid ",
          }}
        >
          Traffic Country
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            margin: "30px",
            padding: "10px",
            border: "2px solid ",
          }}
        >
          Currency
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            margin: "30px",
            padding: "10px",
            border: "2px solid ",
          }}
        >
          Office Country
        </div>
      </div>
    </div>
  );
};

export default MasterTables;
