import React, { useEffect, useState } from "react";
import { Table, Tag, Space } from "antd";
const { Column, ColumnGroup } = Table;

const AllDomain = () => {
  const [data, setData] = useState([]);
  useEffect(async () => {
    const result = await axios(
      "https://hn.algolia.com/api/v1/search?query=redux"
    );

    setData(result.data);
    console.log()
  }, []);

  return (
    <div>
      <Table dataSource={data}>
        <ColumnGroup title="Domain Name">
          <Column title="Company" dataIndex="firstName" key="firstName" />
          <Column title="Website" dataIndex="lastName" key="lastName" />
        </ColumnGroup>
        <Column
          title="Status"
          dataIndex="age"
          key="age"
          render={(age) => (
            <>
              {age.map((tag) => {
                let color = tag.length > 6 ? "volcano" : "green";
                if (tag === "loser") {
                  color = "blue";
                }
                return (
                  <Tag color={color} key={tag}>
                    {tag.toUpperCase()}
                  </Tag>
                );
              })}
            </>
          )}
        />
        <Column title="Type" dataIndex="address" key="address" />
        {/* <Column
          title="Tags"
          dataIndex="tags"
          key="tags"
          render={(tags) => (
            <>
              {tags.map((tag) => {
                let color = tag.length > 5 ? "geekblue" : "purple";
                if (tag === "loser") {
                  color = "blue";
                }
                return (
                  <Tag color={color} key={tag}>
                    {tag.toUpperCase()}
                  </Tag>
                );
              })}
            </>
          )}
        /> */}
        <ColumnGroup title="DA | DR">
          <Column title="DA" dataIndex="DA" key="DA" />
          <Column title="DR" dataIndex="DR" key="DR" />
        </ColumnGroup>
        <Column title="Quality" dataIndex="address" key="address" />
        <Column title="Traffic" dataIndex="address" key="address" />
        <Column title="Sample Links" dataIndex="address" key="address" />
        <Column
          title="Action"
          key="action"
          render={(text, record) => (
            <Space size="middle">
              <a>View</a>
              <a>Edit</a>
              <a>Delete</a>
            </Space>
          )}
        />
      </Table>
    </div>
  );
};

export default AllDomain;
