import React, { useEffect, useState } from "react";
import axios from "axios";
import { Table, Tag, Space, Modal, Button, Tooltip } from "antd";
import Router from "next/router";
const { Column, ColumnGroup } = Table;

const AllDomain = () => {
  const [data, setData] = useState([]);
  const [hasError, setErrors] = useState(false);
  const [viewData, setViewData] = useState([]);
  const [hasViewError, setViewErrors] = useState(false);
  const [isModalVisible, setIsModalVisible] = useState(false);
  useEffect(() => {
    async function fetchData() {
      const res = await fetch("http://localhost:5000/api/v1/domain/");
      res
        .json()
        .then((res) => setData(res))
        .catch((err) => setErrors(err));
    }
    fetchData();
  }, []);

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };
  let wid = "75%";
  return (
    <div style={{ overflow: "scroll" }}>
      {/* <span>{JSON.stringify(data)}</span> */}
      <>
        <Modal
          title="Domain Details"
          visible={isModalVisible}
          onOk={handleOk}
          onCancel={handleCancel}
          width={wid}
          style={{ overflow: "scroll" }}
        >
          <div>
            {/* <span>{JSON.stringify(viewData)}</span> */}
            {console.log(viewData)}
            {viewData.length !== 0 ? (
              <div>
                {console.log(viewData)}
                <div style={{ display: "flex", width: "100%" }}>
                  <div
                    style={{ display: "grid", gridTemplateColumns: "50% 50%" }}
                  >
                    <div style={{ display: "flex" }}>
                      <p style={{ paddingRight: "5px", fontWeight: "bold" }}>
                        NAME:
                      </p>
                      <p
                        style={{
                          paddingRight: "30px",
                          wordBreak: "break-word",
                        }}
                      >
                        {viewData[0].Domain_Name}
                      </p>
                    </div>
                    <div style={{ display: "flex" }}>
                      <Tag
                        color={
                          viewData[0].Domain_Status === "active"
                            ? "green"
                            : "red"
                        }
                        style={{
                          display: "flex",
                          justifyContent: "center",
                          alignItems: "center",
                          maxHeight: "30px",
                          marginRight: "30px",
                          marginTop: "auto",
                          marginBottom: "auto",
                        }}
                      >
                        {viewData[0].Domain_Status}
                      </Tag>
                      <div
                        style={{
                          marginTop: "0px",
                          paddingTop: "0px",
                          marginLeft: "50px",
                        }}
                      >
                        <p
                          style={{
                            textAlign: "center",
                            marginRight: "20px",
                            marginBottom: "5px",
                          }}
                        >
                          DA
                        </p>
                        <p
                          style={{
                            marginRight: "20px",
                            border: "1px solid grey",
                            padding: "5px",
                            borderRadius: "5px",
                          }}
                        >
                          {viewData[0].Domain_DA}
                        </p>
                      </div>
                      <div style={{ marginTop: "0px", paddingTop: "0px" }}>
                        <p
                          style={{
                            textAlign: "center",
                            marginRight: "20px",
                            marginBottom: "5px",
                          }}
                        >
                          DR
                        </p>
                        <p
                          style={{
                            marginRight: "20px",
                            border: "1px solid grey",
                            padding: "5px",
                            borderRadius: "5px",
                          }}
                        >
                          {viewData[0].Domain_DR}
                        </p>
                      </div>
                    </div>
                  </div>
                </div>
                <div style={{ display: "flex" }}>
                  <p style={{ paddingLeft: "10px" }}>
                    {viewData[0].Domain_URL}
                  </p>
                  <p style={{ paddingLeft: "10px" }}>
                    {viewData[0].Domain_Quality}
                  </p>
                </div>
                <div>
                  <p>{viewData[0].Domain_Link_SampleA}</p>
                  <p>{viewData[0].Domain_Link_SampleB}</p>
                  <p>{viewData[0].Domain_Link_SampleC}</p>
                  <p>{viewData[0].Domain_Link_SampleD}</p>
                  <p>{viewData[0].Domain_Link_SampleE}</p>
                  <p>{viewData[0].Domain_Link_SampleF}</p>
                  <p>{viewData[0].Domain_Link_SampleG}</p>
                  <p>{viewData[0].Domain_Link_SampleH}</p>
                  <p>{viewData[0].Domain_Link_SampleI}</p>
                  <p>{viewData[0].Domain_Link_SampleJ}</p>
                </div>
                <div>
                  <p>{viewData[0].Domain_Comments}</p>
                </div>
                <div style={{ display: "flex" }}>
                  <p style={{ paddingLeft: "10px" }}>
                    {viewData[0].Domain_Link_Type}
                  </p>
                  <p style={{ paddingLeft: "10px" }}>
                    {viewData[0].Domain_Link_Attribute}
                  </p>
                  <p style={{ paddingLeft: "10px" }}>
                    {viewData[0].Domain_RefDomCount}
                  </p>
                </div>
                <div>
                  <p>{viewData[0].Domain_Industry}</p>
                </div>
                <h2>Traffic Details</h2>
                <div>
                  <p>{viewData[0].Domain_traffic_Country}</p>
                </div>
                <div style={{ display: "flex" }}>
                  <p style={{ paddingLeft: "10px" }}>
                    {viewData[0].Domain_SEMRush}
                  </p>
                  <p style={{ paddingLeft: "10px" }}>
                    {viewData[0].Domain_AHRef}
                  </p>
                </div>
                <h2>Cost Details</h2>
                <div style={{ display: "flex" }}>
                  <p style={{ paddingLeft: "10px" }}>
                    {viewData[0].Domain_Currency}
                  </p>
                  <p style={{ paddingLeft: "10px" }}>
                    {viewData[0].Domain_Cost_Original}
                  </p>
                  <p style={{ paddingLeft: "10px" }}>
                    {viewData[0].Domain_Cost_Nego}
                  </p>
                </div>
                <div>
                  <p>{viewData[0].Domain_Office_Country}</p>
                </div>
                <div>
                  <p>{viewData[0].Domain_Cost_Comments}</p>
                </div>
                <h2>Contact Details</h2>
                <div>
                  <p>{viewData[0].Domain_Contact_Person}</p>
                </div>
                <div>
                  <p>{viewData[0].Domain_Contact_EmailA}</p>
                  <p>{viewData[0].Domain_Contact_EmailB}</p>
                </div>
                <div>
                  <p>{viewData[0].Domain_Contact_Phone}</p>
                </div>
                <h2>Time Details</h2>
                <div>
                  <p>{viewData[0].Creation_Date}</p>
                  <p>{viewData[0].Updation_Date}</p>
                </div>
              </div>
            ) : (
              <p>No data found</p>
            )}
          </div>
        </Modal>
      </>
      <Table dataSource={data}>
        <ColumnGroup title="Domain Name">
          {/* <Column title="Company" dataIndex="Domain_Name" key="Domain_Name"  /> */}
          <Tooltip placement="topLeft" title="Company">
            {dataSource.Domain_Name}
          </Tooltip>
          <Column title="Website" dataIndex="Domain_URL" key="Domain_URL" />
        </ColumnGroup>
        <Column
          title="Status"
          dataIndex="Domain_Status"
          key="Domain_Status"
          render={(Domain_Status) => (
            <>
              <Tag
                color={Domain_Status === "active" ? "green" : "red"}
                key={Domain_Status}
              >
                {Domain_Status.toUpperCase()}
              </Tag>
            </>
          )}
        />
        <Column
          title="Type"
          dataIndex="Domain_Link_Type"
          key="Domain_Link_Type"
        />
        <ColumnGroup title="DA | DR">
          <Column title="DA" dataIndex="Domain_DA" key="Domain_DA" />
          <Column title="DR" dataIndex="Domain_DR" key="Domain_DR" />
        </ColumnGroup>
        <Column
          title="Quality"
          dataIndex="Domain_Quality"
          key="Domain_Quality"
        />
        <ColumnGroup title="Traffic">
          <Column
            title="SEMRush"
            dataIndex="Domain_SEMRush"
            key="Domain_SEMRush"
          />
          <Column title="AHRef" dataIndex="Domain_AHRef" key="Domain_AHRef" />
        </ColumnGroup>
        <Column
          title="Sample Links"
          dataIndex="Domain_Link_SampleA"
          key="Domain_Link_SampleA"
        />
        <Column
          title="Action"
          key="action"
          render={(data) => (
            <Space size="middle">
              <a
                onClick={() => {
                  async function fetchData() {
                    const resV = await fetch(
                      `http://localhost:5000/api/v1/domain/${data.DomainID}`
                    );
                    resV
                      .json()
                      .then((resV) => setViewData(resV))
                      .catch((err) => setViewErrors(err));
                  }
                  fetchData().then(() => {
                    setIsModalVisible(true);
                  });
                }}
              >
                View
              </a>
              <a
                onClick={() => {
                  console.log(data.DomainID);
                }}
              >
                Edit
              </a>
              <a
                onClick={() => {
                  async function fetchDataDel() {
                    const resV = await fetch(
                      `http://localhost:5000/api/v1/domain/${data.DomainID}`,
                      { method: "DELETE" }
                    )
                      .then((res) => res.text()) // or res.json()
                      .then((res) => console.log(res));
                  }
                  fetchDataDel().then(() => {
                    window.location.reload(false);
                  });
                }}
              >
                Delete
              </a>
            </Space>
          )}
        />
      </Table>
    </div>
  );
};

export default AllDomain;

{
  /* <Column
          title="Tags"
          dataIndex="tags"
          key="tags"
          render={(tags) => (
            <>
              {tags.map((tag) => {
                let color = tag.length > 5 ? "geekblue" : "purple";
                if (tag === "loser") {
                  color = "blue";
                }
                return (
                  <Tag color={color} key={tag}>
                    {tag.toUpperCase()}
                  </Tag>
                );
              })}
            </>
          )}
        /> */
}
