import React from "react";
import { Table, Tag, Space } from "antd";
const { Column, ColumnGroup } = Table;

const AllDomain = () => {
  const data = [
    {
      key: "1",
      firstName: "John",
      lastName: "Brown",
      age: "active",
      address: "New York No. 1 Lake Park",
      tags: ["nice", "developer"],
    },
    {
      key: "2",
      firstName: "Jim",
      lastName: "Green",
      age: "inactive",
      address: "London No. 1 Lake Park",
      tags: ["loser"],
    },
    {
      key: "3",
      firstName: "Joe",
      lastName: "Black",
      age: "active",
      address: "Sidney No. 1 Lake Park",
      tags: ["cool", "teacher"],
    },
  ];

  return (
    <div>
      <Table dataSource={data}>
        <ColumnGroup title="Domain Name">
          <Column title="Company" dataIndex="firstName" key="firstName" />
          <Column title="Website" dataIndex="lastName" key="lastName" />
        </ColumnGroup>
        <Column title="Status" dataIndex="age" key="age" render={(age)=>{
			if(age==="active"){
				color = "green";
			}
		}} />
        <Column title="Address" dataIndex="address" key="address" />
        <Column
          title="Tags"
          dataIndex="tags"
          key="tags"
          render={(tags) => (
            <>
              {tags.map((tag) => {
                let color = tag.length > 5 ? "geekblue" : "purple";
                if (tag === "loser") {
                  color = "blue";
                }
                return (
                  <Tag color={color} key={tag}>
                    {tag.toUpperCase()}
                  </Tag>
                );
              })}
            </>
          )}
        />
        <Column
          title="Action"
          key="action"
          render={(text, record) => (
            <Space size="middle">
              <a>View</a>
              <a>Edit</a>
              <a>Delete</a>
            </Space>
          )}
        />
      </Table>
    </div>
  );
};

export default AllDomain;
