import React, { useEffect, useState } from "react";
import axios from "axios";
import { Table, Tag, Space } from "antd";
const { Column, ColumnGroup } = Table;

const AllDomain = () => {
  const [data, setData] = useState([]);
  const [hasError, setErrors] = useState(false);
  useEffect(() => {
    async function fetchData() {
      const res = await fetch("http://localhost:5000/api/v1/domain/");
      res
        .json()
        .then((res) => setData(res))
        .catch((err) => setErrors(err));
    }
    fetchData();
  }, []);
  let color;
  return (
    <div style={{ overflow: "scroll" }}>
      <span>{JSON.stringify(data)}</span>
      <Table dataSource={data}>
        <ColumnGroup title="Domain Name">
          <Column title="Company" dataIndex="Domain_Name" key="Domain_Name" />
          <Column title="Website" dataIndex="Domain_URL" key="Domain_URL" />
        </ColumnGroup>
        <Column
          title="Status"
          dataIndex="Domain_Status"
          key="Domain_Status"
          render={(Domain_Status) => (
            <>
              {Domain_Status === "active" ? (color = "green") : (color = "red")}
              <Tag color={color} key={Domain_Status}>
                {Domain_Status.toUpperCase()}
              </Tag>
            </>
          )}
        />
      </Table>
      {/*
        <Column title="Type" dataIndex="Domain_Link_Type" key="Domain_Link_Type" />
        <ColumnGroup title="DA | DR">
          <Column title="DA" dataIndex="Domain_DA" key="Domain_DA" />
          <Column title="DR" dataIndex="Domain_DR" key="Domain_DR" />
        </ColumnGroup>
        <Column title="Quality" dataIndex="Domain_Quality" key="Domain_Quality" />
        <ColumnGroup title="Traffic">
          <Column title="SEMRush" dataIndex="Domain_DR" key="Domain_DR" />
          <Column title="AHRef" dataIndex="" key="" />
        </ColumnGroup>
        <Column title="Sample Links" dataIndex="address" key="address" />
        <Column
          title="Action"
          key="action"
          render={(text, record) => (
            <Space size="middle">
              <a>View</a>
              <a>Edit</a>
              <a>Delete</a>
            </Space>
          )}
        />
      </Table> */}
    </div>
  );
};

export default AllDomain;

